function trazar(){new Vivus("heroSvg",{duration:250,type:"oneByOne",pathTimingFunction:Vivus.EASE_OUT},()=>{$("#heroSvg").addClass("terminado")})}function tipear(){new TypeIt("#escritura",{strings:["ELEGANTES","MODERNAS","RESPONSIVE"],speed:100,deleteSpeed:50,loop:!0,breakLines:!1})}function carrusel2(){$("#carruselClientes2").owlCarousel({items:3,margin:30,autoplay:!0,autoplayTimeout:2e3,loop:!0,autoplayHoverPause:!0})}function quitarSpinner(){$(".spinner").fadeOut(1e3,()=>{$("h1").animate({"letter-spacing":"15px"},800)})}$(window).on("scroll",()=>{const o=$(window).scrollTop(),e=`${.1*o}deg`;$("#logo").css("transform",`rotate(${e})`),$(".reveal").each((function(){const e=$(this).offset().top,i=.7*window.innerHeight;o>=e-i&&$(this).addClass("visible")}))}),window.onload=()=>{trazar(),tipear(),carrusel2(),quitarSpinner()},$("#playVid, #iconoPlayVid").on("click",()=>{$("#videoPresentacion").get(0).play(),$("#iconoPlayVid").css("opacity",0)}),$("#pauseVid").on("click",()=>{$("#videoPresentacion").get(0).pause(),$("#iconoPlayVid").css("opacity",1)}),$("#stopVid").on("click",()=>{$("#videoPresentacion").get(0).load(),$("#iconoPlayVid").css("opacity",1)}),$(".nav-link, .button-link").on("click",(function(){const o=$(this).attr("href");$("html").animate({scrollTop:$(o).offset().top},1e3)}));